package com.cxg.entity;import java.util.Date;import javax.persistence.Table;import javax.persistence.Transient;/******************************************************************************* * 用户信息表（t_cust_user） * @author 2017-02-12 16:44:39 *  */	@Table(name="t_cust_user")public class CustUserBean implements java.io.Serializable {	//field	/**  **/	private Long id;	/** 手机号 **/	private String phone;	/** 登录密码 **/	private String password;	/** 姓名 **/	private String name;	/** 邮箱 **/	private String email;	/** 创建时间 **/	private Date createTime;	/** 是否已经删除: N-未删除,Y-已删除 **/	private String isDelete;	/** 备注 **/	private String remark;	/**上回操作公司**/	private Long lastCompanyBy;	
	/**邮箱是否已认证：N-未认证,Y-已认证*/	private String isEmail;			/**辅助字段开始 **/
	/**用户头像**/    private String headPortrait;    
	/** 用户角色**/	@Transient	private String userRole;	/** 用户角色名**/	@Transient	private String userRoleName;		/**	 * 用户角色标识	 */	@Transient	private String userRoleFlag;   			//method	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getPhone() {		return phone;	}	public void setPhone(String phone) {		this.phone = phone;	}	public String getPassword() {		return password;	}	public void setPassword(String password) {		this.password = password;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getEmail() {		return email;	}	public void setEmail(String email) {		this.email = email;	}	public Date getCreateTime() {		return createTime;	}	public void setCreateTime(Date createTime) {		this.createTime = createTime;	}	public String getIsDelete() {		return isDelete;	}	public void setIsDelete(String isDelete) {		this.isDelete = isDelete;	}	public String getRemark() {		return remark;	}	public void setRemark(String remark) {		this.remark = remark;	}		public Long getLastCompanyBy() {        return lastCompanyBy;    }    public void setLastCompanyBy(Long lastCompanyBy) {        this.lastCompanyBy = lastCompanyBy;    }        public String getUserRole() {		return userRole;	}	public void setUserRole(String userRole) {		this.userRole = userRole;	}	public String getUserRoleName() {		return userRoleName;	}	public void setUserRoleName(String userRoleName) {		this.userRoleName = userRoleName;	}
		public String getIsEmail() {		return isEmail;	}	public void setIsEmail(String isEmail) {		this.isEmail = isEmail;	}	public String getHeadPortrait() {        return headPortrait;    }    public void setHeadPortrait(String headPortrait) {        this.headPortrait = headPortrait;    }            public String getUserRoleFlag() {        return userRoleFlag;    }    public void setUserRoleFlag(String userRoleFlag) {        this.userRoleFlag = userRoleFlag;    }    @Override
    public String toString() {        return "CustUserBean [id=" + id + ", phone=" + phone + ", password=" + password + ", name=" + name + ", email=" + email + ", createTime=" + createTime + ", isDelete=" + isDelete + ", remark="                + remark + ", lastCompanyBy=" + lastCompanyBy + ", userRole=" + userRole+ ", userRoleName=" + userRoleName + "]";    }  	}